from typing import List
from random import randint


class Solution2:
    def fairCandySwap(self, A, B):
        A.sort()
        B.sort()
        left = right = 0
        diff = (sum(A) - sum(B)) / 2
        while True:
            temp = A[left] - B[right]
            if temp > diff:
                right += 1
            elif temp < diff:
                left += 1
            else:
                return [A[left], B[right]]


class Solution:
    @staticmethod
    def binary_search(arr, low, high, val):
        if high >= low:
            mid = (high + low) // 2
            if arr[mid] == val:
                return val

            elif arr[mid] > val:
                return Solution.binary_search(arr, low, mid - 1, val)
            elif arr[mid] < val:
                return Solution.binary_search(arr, mid + 1, high, val)
        else:
            return None

    @staticmethod
    def quicksort(A):
        if len(A) <= 1:
            return A
        val = A[randint(0, len(A) - 1)]
        less, equal, more = [], [], []
        for a in A:
            if a == val:
                equal.append(a)
            elif a > val:
                more.append(a)
            else:
                less.append(a)
        return Solution.quicksort(less) + equal + Solution.quicksort(more)

    def fairCandySwap(self, A: List[int], B: List[int]) -> List[int]:
        """
        sum(A) - a + b = sum(B) - b + a
        -a + b + b - a = sum(B) - sum(A)
        -2a + 2b = sum(B) - sum(A)
        -2a = (sum(B) - sum(A) - 2b)
        -a = (sum(B) - sum(A)) / 2 - b
        a = (sum(A) - sum(B)) / 2 + b
        b = a - (sum(A) - sum(B)) / 2
        """
        half_of_A_B_diff = (sum(A) - sum(B)) / 2
        A = self.quicksort(list(set(A)))
        B = self.quicksort(list(set(B)))
        for a in set(A):
            b = a - half_of_A_B_diff
            if Solution.binary_search(B, 0, len(B) - 1, b):
                return [a, b]


